import './App.css';
import {useEffect} from "react";

// https://assets.codepen.io/1082881/data.json
// 1. Fetch the data from endpoint
// 2. Get the items array from the data
// 3. Filter items array to only contain products on sale (hasDiscount === true)
// 4. Loop over items, generate HTML with JS, and insert into the dom


function App() {


    const [cards, setCards] = useState([]);

    useEffect(() => {
        fetch('https://assets.codepen.io/1082881/data.json')
            .then(response => response.json())
            .then(data => {
                const items = data.items;
                const filteredItems = items.filter(item => item.hasDiscount === true);
                setCards(filteredItems);
            });
    }, []);



  return (
    <div className="App">
      <h1>Front-end Code Test</h1>
      <h3>Parse and Render Test</h3>
        <div>
            {cards.map(card => {
                return (
                    <div className="item">
                        <img src={card.image} alt={card.title}/>
                        <h3>{card.title}</h3>
                        <p>{card.description}</p>
                        <p>{card.price}</p>
                    </div>
                )
            })}
        </div>
    </div>
  );
}

export default App;
